import React, { useEffect, useState } from 'react'
import CategoryService from '../services/CategoryService'
import ProductService from '../services/ProductService'

export const Home = () => {
    const [loadingCategories, setLoadingCategories] = useState(false)
    const [categories, setCategories] = useState(null)
    const [products, setProducts] = useState(null)
    const [loadingProducts, setLoadingProducts] = useState(false)
    const [message, setMessage] = useState(null)

    useEffect(() => {
      setLoadingCategories(true);
      CategoryService.getAll()
      .then(({data}) => {
        setCategories(data);
        let event = new Event('change');
        document.getElementById("category-name").value = '';
        document.getElementById("category-name").dispatchEvent(event);
      })
      .catch(err => console.dir(err))
      .finally(() => setLoadingCategories(false))      
    }, [])

    const handleChange = e => {
      setLoadingProducts(true);
      setMessage("Obteniendo productos...");
      ProductService.getByCategory(e.target.value)
          .then(({ data }) => {
            setProducts(data)
          })
          .catch(err => console.dir(err))
          .finally(() => {
            setLoadingProducts(false); 
            setMessage(null)
          }) 
    };

    return (  
      <section className="container py-3">
        <div className='row row-cols-1'>
          {loadingCategories && "Obteniendo categorías..."}
          <div className="form-floating">
            <select className="form-select form-select-lg mb-3"
            id='category-name'
            name='category-name'
            onChange={handleChange.bind(this)}>
              <option value={""} defaultValue>Selecciona una categoría</option>
              {
              !!categories && categories.length > 0?            
                categories.map((category)=> (
                  <option key={category} value={category}>
                    {category}
                  </option>
                )):[]
              }
            </select>
            <label for="category-name">Categoría de producto</label> 
          </div>
        </div>
        <div className="row">
        <br/>
        {loadingProducts && message}
        <br/>
        {!!products && products.length > 0 ? products.map((product) => {
            return(              
                <div key={product.id} className="card mb-3">
                    <div className="row g-0">
                      <div className="col-md-4">
                        <img className="img-fluid rounded-start card-img-product" src={product.image} alt={product.title}/>
                      </div>
                      <div className="col-md-8">
                        <div className="card-body">
                          <h5 className="card-title">{product.title}</h5>
                          <p className="card-text">{product.description}</p>
                          <p className="card-text"><small className="text-muted">Precio: {product.price}</small></p>                   
                        </div>                    
                      </div>
                    </div>
                </div>
            )   
          }):(<p></p>)
        }
        </div> 
        
      </section>
    )
}
